#!/bin/bash
screen -dmS train bash -c 'source /home/lyk/.bash_profile; cd /home/lyk/machine_learning/Supervised_Learning/tensorflow_models/attention_ocr/python; python train.py --dataset_name=han_dataset_2 --optimizer=adam --batch_size=8 --num_lstm_units=512 --use_augment_input=false --train_log_dir=./aocr; exec bash'
screen -dmS eval_train bash -c 'source /home/lyk/.bash_profile; cd /home/lyk/machine_learning/Supervised_Learning/tensorflow_models/attention_ocr/python; sleep 1800; CUDA_VISIBLE_DEVICES=0 python eval.py --eval_interval_secs=3600 --batch_size=16 --num_batches 10000 --num_lstm_units=512 --dataset_name=han_dataset_2 --train_log_dir=./aocr --eval_log_dir=./aocr_eval_train; exec bash'
screen -dmS eval_test bash -c 'source /home/lyk/.bash_profile; cd /home/lyk/machine_learning/Supervised_Learning/tensorflow_models/attention_ocr/python; sleep 3600; CUDA_VISIBLE_DEVICES=0 python eval.py --eval_interval_secs=3600 --batch_size=16 --num_batches 10000 --num_lstm_units=512 --dataset_name=han_dataset_2 --train_log_dir=./aocr --eval_log_dir=./aocr_eval_test --split_name=test; exec bash'
screen -dmS tensorboard_train bash -c 'source /home/lyk/.bash_profile; cd /home/lyk/machine_learning/Supervised_Learning/tensorflow_models/attention_ocr/python; tensorboard --logdir=./aocr --port=6006'
screen -dmS tensorboard_eval_train bash -c 'source /home/lyk/.bash_profile; cd /home/lyk/machine_learning/Supervised_Learning/tensorflow_models/attention_ocr/python; tensorboard --logdir=./aocr_eval_train --port=6007'
screen -dmS tensorboard_eval_test bash -c 'source /home/lyk/.bash_profile; cd /home/lyk/machine_learning/Supervised_Learning/tensorflow_models/attention_ocr/python; tensorboard --logdir=./aocr_eval_test --port=6008'
